/*
小米运动修改微信支付宝运动步数
APP Store下载小米运动APP
登入小米运动(登录方式必须是手机号码+密码(没有就用手机号码注册),下面的第三方账号(小米账号,Apple,微信)授权登录不行)
登录成功后在 我的->第三方接入->绑定支付宝,微信
小米运动只要不退出登录，就会自动获取新的token,即永久有效
[MITM]
hostname = *.huami.com
Surge
[Script]
小米运动 = type=cron,cronexp="15 17 * * *",wake-system=1,timeout=3600,script-path=xmSports.js
小米运动获取Token = type=http-request,pattern=https:\/\/account(\-cn2)?\.huami\.com(.*)\/login, requires-body=1, max-size=0, script-path=xmSports.js
圈X
[task_local]
# 小米运动
15 17 * * * xmSports.js, tag=小米运动, img-url=https://raw.githubusercontent.com/58xinian/icon/master/xmyd.png, enabled=true
[rewrite_local]
# 小米运动获取Token
https:\/\/account(\-cn2)?\.huami\.com(.*)\/login url script-request-body xmSports.js
Loon
[Script]
cron "15 17 * * *" script-path=xmSports.js, tag=小米运动
http-request https:\/\/account(\-cn2)?\.huami\.com(.*)\/login script-path=xmSports.js, requires-body=true, timeout=3600, tag=小米运动获取Token
 */

const $ = new API('xmSports');
$.token = $.read('token') || {};

$.notify = $.env.isNode
  ? (title, sub, content) => {
      require('./sendNotify').sendNotify(title, `${sub}\n${content}`);
    }
  : $.notify;
if ($.env.isNode && !$.token.length) {
  if (process.env.XM_SPORTS_TOKEN) {
    $.token = process.env.XM_SPORTS_TOKEN.split('&');
  }
}
$.message = '';

(async () => {
  try {
    if ($request.url.indexOf('ctype') === -1) return getToken();
  } catch (error) {}
  $.userIds = Object.keys($.token);
  if (!$.userIds.length) {
    return ($.message = '请获取正确的登陆信息');
  }
  $.message = '⭕️开始小米同步';

  const tokenInfo = {};
  for (let index = 0; index < $.userIds.length; index++) {
    const item = $.token[$.userIds[index]];
    $.body = item.login_token;
    const response = await login($.body);
    if (response.result === 'ok') {
      tokenInfo[$.userIds[index]] = response.token_info;
    } else {
      console.log(item.phone || $.userIds[index] + ' TOKEN 过期');
    }
  }
  const userIds = Object.keys(tokenInfo);
  for (let index = 0; index < userIds.length; index++) {
    $.step = randomFriendPin(
      $.read('minStep') * 1 || 20000,
      $.read('maxStep') * 1 || 23000,
    );
    $.user_id = userIds[index];
    $.app_token = tokenInfo[$.user_id].app_token;
    if ($.user_id && $.app_token) {
      $.message += `\n账号：${$.user_id}`;
      await get_time();
      const res = await change_step();
      if (res.code === 1) {
        $.message += `\n结果：${$.step}步同步成功\n同步时间：${timeFormat(
          localtime(),
        )}\n‍`;
      } else {
        $.message += `\n结果：步数同步失败${JSON.stringify(res)}`;
      }
    }
  }
})()
  .catch((e) => {
    console.log(JSON.stringify(e));
  })
  .finally(() => {
    if ($.message) $.notify('小米运动', '', $.message);
    $.done({});
  });

function getToken() {
  if ($response.body) {
    const body = JSON.parse($response.body);
    const loginToken = body.token_info.login_token;
    const userId = body.token_info.user_id;
    $.token[userId] = loginToken;
    $.message = `获取 TOKEN 成功\nPs:新增成功之后进入登录的接口全部失效会再重新登陆一次`;
    $.write($.token, 'token');
  }
  return $.message;
}

async function login(login_token) {
  const options = {
    url: `https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token=${login_token}&os_version=4.1.0`,
  };
  return $.http.get(options).then((response) => JSON.parse(response.body));
}

